components/f/hero/carousel:
{{#if theme_settings.hero_width '===' 'contain'}}<div class="container hero">{{/if}}
    <section class="heroCarousel"
        data-slick='{
            "arrows": {{arrows}},
            "mobileFirst": true,
            "slidesToShow": 1,
            "slidesToScroll": 1,
            "autoplay": true,
            "autoplaySpeed": {{carousel.swap_frequency}},
            "lazyLoad": "anticipated"
        }'>
        {{#each carousel.slides}}
        <a href="{{url}}">
            <div class="heroCarousel-slide {{../theme_settings.hero_align}} {{#if ../theme_settings.homepage_stretch_carousel_images}}stretch{{/if}} {{#if @first}}heroCarousel-slide--first{{/if}}">
                <div class="heroCarousel-image-wrapper" {{#and image_height image_width}}style="height: {{multiply (divide image_height image_width) 90}}vw"{{/and}}>
                    <img class="heroCarousel-image" {{#if @first}}src{{else}}data-lazy{{/if}}="{{image}}" alt="{{alt_text}}" title="{{alt_text}}"
                    {{#if image_width}}width="{{image_width}}"{{/if}} {{#if image_height}}height="{{image_height}}"{{/if}} />
                </div>
                {{#if heading}}
                    {{> components/f/hero/carousel-content show_background=true theme_settings=../../theme_settings}}
                {{else}}
                    {{#if text}}
                        {{> components/f/hero/carousel-content show_background=true theme_settings=../../../theme_settings}}
                    {{else}}
                        {{#if button_text}}
                            {{> components/f/hero/carousel-content show_background=false theme_settings=../../../../theme_settings}}
                        {{/if}}
                    {{/if}}
                {{/if}}
            </div>
        </a>
        {{/each}}
    </section>
{{#if theme_settings.hero_width '===' 'contain'}}</div>{{/if}}
